if(!String.prototype.isEmpty)String.prototype.isEmpty=function(){return!this||this==""||this==void 0||this==NaN||this==null||this.length<1||this=="undefined"||typeof this==void 0||typeof this==NaN};if(!String.prototype.startsWith)String.prototype.startsWith=function(a){return!this.indexOf(a)};if(!String.prototype.endsWith)String.prototype.endsWith=function(a){return this.indexOf(a,this.length-a.length)!==-1};
if(!String.prototype.contains)String.prototype.contains=function(a){return this.indexOf(a)!=-1};if(!String.prototype.equals)String.prototype.equals=function(a){return this==a};if(!String.prototype.equalsIgnoreCase)String.prototype.equalsIgnoreCase=function(a){return this==a?!0:a!=null&&a.length==this.length&&this.toLowerCase()==a.toLowerCase()};if(!String.prototype.trim)String.prototype.trim=function(){return this.replace(/^\s\s*/,"").replace(/\s\s*$/,"")};